mapper.select:
 	SELECT mem_id
 	  FROM member
	 WHERE mem_id = #{ memId }

 	SELECT mem_email
 	  FROM member
	 WHERE mem_email = #{ memEmail }

 	SELECT mem_nickname
 	  FROM member
	 WHERE mem_nickname = #{ memNickname }

SELECT mem_id
  FROM member
 WHERE mem_phone = #{ memPhone } 
   AND mem_name = #{ memName };

SELECT mem_id, mem_pw
  FROM member
 WHERE mem_email = #{ memEmail } 
   AND mem_name = #{ memName };
